import React, { useState, useEffect, useCallback } from 'react';
import { PreferenceSorter } from './utils/sortingAlgorithm';
import './index.css';

function App() {
  const [userName, setUserName] = useState('');
  const [nameOptions, setNameOptions] = useState([]);
  const [currentPair, setCurrentPair] = useState({ left: null, right: null });
  const [isSorting, setIsSorting] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [results, setResults] = useState([]);
  const [sorter, setSorter] = useState(null);
  const [progress, setProgress] = useState(0);
  const [totalComparisons, setTotalComparisons] = useState(0);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchOptions = async () => {
      setIsLoading(true);
      try {
        const response = await fetch('/options.txt');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.text();
        const optionsArray = data.split('\n').filter(option => option.trim() !== '');
        if (optionsArray.length > 1) {
          setNameOptions(shuffleArray(optionsArray));
        } else {
          console.error('Insufficient options to perform sorting.');
        }
        setIsLoading(false);
      } catch (error) {
        console.error('Error loading options:', error);
        setIsLoading(false);
      }
    };
    fetchOptions();
  }, []);

  const handleChoice = useCallback((choice) => {
    if (!window.resolveComparison) {
      return;
    }

    switch (choice) {
      case 'left':
        window.resolveComparison(-1);
        break;
      case 'right':
        window.resolveComparison(1);
        break;
      case 'both':
        window.resolveComparison(0);
        break;
      case 'none':
        window.resolveComparison(0);
        break;
      default:
        break;
    }
  }, []); // Empty dependency array since it doesn't depend on any state/props

  useEffect(() => {
    const handleKeyPress = (event) => {
      if (!isSorting) {
        return;
      }

      switch(event.key) {
        case 'ArrowLeft':
          handleChoice('left');
          break;
        case 'ArrowRight':
          handleChoice('right');
          break;
        case 'b':
        case 'B':
          handleChoice('both');
          break;
        case 'n':
        case 'N':
          handleChoice('none');
          break;
        default:
          break;
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [isSorting, handleChoice]);

  const handleStart = async () => {
    if (userName.trim() === '') {
      alert('Please enter your name to start the quiz.');
      return;
    }
    if (nameOptions.length < 2) {
      alert('Not enough options to start sorting.');
      return;
    }
    const newSorter = new PreferenceSorter(nameOptions);
    setSorter(newSorter);
    setIsSorting(true);
    const n = nameOptions.length;
    const estimatedComparisons = Math.ceil(n * Math.log2(n));
    setTotalComparisons(estimatedComparisons);
    setProgress(0);

    try {
      let comparisons = 0;
      const sortedResults = await newSorter.sort(async (a, b) => {
        setCurrentPair({ left: a, right: b });
        comparisons++;
        setProgress(Math.floor((comparisons / estimatedComparisons) * 100));
        return new Promise((resolve) => {
          window.resolveComparison = resolve;
        });
      });
      setResults(sortedResults);
      setShowResults(true);
      setIsSorting(false);
    } catch (error) {
      console.error('Sorting error:', error);
    }
  };

  const shuffleArray = (array) => {
    // Fisher-Yates shuffle algorithm
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
  };

  const handleRedo = () => {
    setCurrentPair({ left: null, right: null });
    setIsSorting(false);
    setShowResults(false);
    setResults([]);
    setSorter(null);
    setNameOptions(shuffleArray([...nameOptions]));
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Help Me Name My Cat!</h1>
        <p>Welcome to the ultimate cat name sorter! Please help me decide on the perfect name by choosing your favorite option in each matchup.</p>
      </header>

      {isLoading && (
        <div className="loading">
          <p>Loading name options...</p>
        </div>
      )}

      {!isSorting && !showResults && nameOptions.length < 2 && (
        <div className="error-message">
          <p>Unable to start sorting. Insufficient options available.</p>
        </div>
      )}

      {!isSorting && !showResults && (
        <div className="user-input">
          <label htmlFor="userName">Enter your name:</label>
          <input 
            type="text" 
            id="userName" 
            value={userName} 
            onChange={(e) => setUserName(e.target.value)} 
            placeholder="Your Name" 
            required 
          />
          <button onClick={handleStart}>Start Sorting</button>
        </div>
      )}

      {isSorting && currentPair.left && currentPair.right && (
        <div className="quiz-section">
          <div className="progress-bar">
            <div 
              className="progress-fill" 
              style={{width: `${progress}%`}}
            ></div>
            <span>{progress}% complete</span>
          </div>
          <h2>Matchup ({Math.floor(progress)}% Complete)</h2>
          <div className="matchup">
            <button 
              onClick={() => handleChoice('left')}
              title="Press ← arrow key"
            >
              {currentPair.left}
            </button>
            <button 
              onClick={() => handleChoice('right')}
              title="Press → arrow key"
            >
              {currentPair.right}
            </button>
          </div>
          <div className="middleField">
            <button 
              onClick={() => handleChoice('both')}
              title="Press 'B' key"
            >
              I like both
            </button>
            <button 
              onClick={() => handleChoice('none')}
              title="Press 'N' key"
            >
              No Opinion
            </button>
          </div>
          <div className="keyboard-hints">
            <p>Keyboard shortcuts: ← Left, → Right, B (Both), N (No Opinion)</p>
          </div>
        </div>
      )}

      {showResults && (
        <div className="results-section">
          <h2>Results</h2>
          <ul>
            {results.map((result, index) => (
              <li key={index}>{result}</li>
            ))}
          </ul>
          <button onClick={handleRedo}>Redo Bracket</button>
        </div>
      )}

      <footer>
        <p>Made with ❤️ by a fellow cat lover</p>
      </footer>
    </div>
  );
}

export default App;
