import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';
import './RankingAdjustment.css';

function RankingAdjustment({ rankings, onSave, onCancel, userName }) {
  const [items, setItems] = useState(() => 
    rankings.map(item => ({
      name: typeof item.name === 'string' ? item.name : String(item.name),
      rating: typeof item.rating === 'number' ? item.rating : 1500
    }))
  );
  const [saveStatus, setSaveStatus] = useState('');
  const [isDragging, setIsDragging] = useState(false);

  useEffect(() => {
    if (items !== rankings) {
      const saveData = async () => {
        setSaveStatus('Saving...');
        try {
          await onSave(items);
          setSaveStatus('Saved ✓');
          setTimeout(() => setSaveStatus(''), 2000);
        } catch (error) {
          setSaveStatus('Save failed! Try again later');
          setTimeout(() => setSaveStatus(''), 3000);
        }
      };

      // Debounce save operations
      const timeoutId = setTimeout(saveData, 500);
      return () => clearTimeout(timeoutId);
    }
  }, [items, rankings, onSave]);

  const handleDragStart = () => {
    setIsDragging(true);
  };

  const handleDragEnd = (result) => {
    setIsDragging(false);
    if (!result.destination) return;

    const newItems = Array.from(items);
    const [reorderedItem] = newItems.splice(result.source.index, 1);
    newItems.splice(result.destination.index, 0, reorderedItem);

    // Enhanced rating calculation with smoother distribution
    const maxRating = 2000;
    const minRating = 1000;
    const ratingStep = (maxRating - minRating) / (newItems.length - 1);

    const adjustedItems = newItems.map((item, index) => ({
      ...item,
      rating: Math.round(maxRating - (index * ratingStep))
    }));

    setItems(adjustedItems);
  };

  return (
    <div className="ranking-adjustment">
      <div className="ranking-header">
        <h2>Your Cat Name Rankings</h2>
        <p className="welcome-text">
          Adjust your name rankings here{userName ? `, ${userName}` : ''}! Drag and drop to reorder names.
        </p>
      </div>

      <div className={`save-status-indicator ${saveStatus ? 'visible' : ''}`}>
        <span className={`save-status ${saveStatus.includes('failed') ? 'error' : 'success'}`}>
          {saveStatus}
        </span>
      </div>

      <div className="rankings-container">
        <div className="rankings-header">
          <div className="rank-header">Rank</div>
          <div className="name-header">Name</div>
          <div className="rating-header">Rating</div>
        </div>

        <DragDropContext 
          onDragStart={handleDragStart}
          onDragEnd={handleDragEnd}
        >
          <Droppable droppableId="rankings">
            {(provided, snapshot) => (
              <div
                {...provided.droppableProps}
                ref={provided.innerRef}
                className={`rankings-list ${snapshot.isDraggingOver ? 'dragging-over' : ''}`}
              >
                {items.map((item, index) => (
                  <Draggable 
                    key={item.name} 
                    draggableId={item.name} 
                    index={index}
                  >
                    {(provided, snapshot) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        className={`ranking-card ${snapshot.isDragging ? 'dragging' : ''}`}
                      >
                        <div className="rank-badge">
                          {index + 1}
                        </div>
                        <div 
                          className="card-content"
                          {...provided.dragHandleProps}
                        >
                          <h3 className="name">{item.name}</h3>
                          <div className="rating">
                            <span className="rating-label">Rating:</span>
                            <span className="rating-value">{item.rating}</span>
                          </div>
                        </div>
                        <div className="drag-indicator">
                          <svg viewBox="0 0 24 24" width="24" height="24">
                            <path fill="currentColor" d="M8 18h8v-2H8v2zm0-4h8v-2H8v2zm0-4h8V8H8v2zm0-4h8V4H8v2z"/>
                          </svg>
                        </div>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </DragDropContext>
      </div>

      <div className="adjustment-controls">
        <button 
          onClick={onCancel} 
          className="back-button"
          disabled={isDragging}
        >
          Back to Tournament
        </button>
        <button 
          onClick={() => window.location.reload()} 
          className="new-tournament-button"
          disabled={isDragging}
        >
          Start New Tournament
        </button>
      </div>
    </div>
  );
}

export default RankingAdjustment;
