import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [userName, setUserName] = useState('');
  const [options, setOptions] = useState([]);
  const [currentMatchup, setCurrentMatchup] = useState({ left: '', right: '' });
  const [results, setResults] = useState([]);
  const [isSorting, setIsSorting] = useState(false);

  useEffect(() => {
    fetch('/options.txt')
      .then(response => response.text())
      .then(data => {
        const optionsArray = data.split('\n').filter(option => option.trim() !== '');
        setOptions(optionsArray);
        if (optionsArray.length > 1) {
          setCurrentMatchup({ left: optionsArray[0], right: optionsArray[1] });
        }
      })
      .catch(error => console.error('Error loading options:', error));
  }, []);

  const handleStart = () => {
    if (userName.trim() === '') {
      alert('Please enter your name to start the quiz.');
      return;
    }
    setIsSorting(true);
  };

  const handleChoice = (choice) => {
    // Logic to handle the choice and update the state
    console.log('User chose:', choice);
    // Move to the next matchup or finish
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Help Me Name My Cat!</h1>
        <p>Welcome to the ultimate cat name sorter! Please help me decide on the perfect name by choosing your favorite option in each matchup.</p>
      </header>
      {!isSorting ? (
        <div className="user-input">
          <label htmlFor="userName">Enter your name:</label>
          <input type="text" id="userName" value={userName} onChange={(e) => setUserName(e.target.value)} placeholder="Your Name" required />
          <button onClick={handleStart}>Start Sorting</button>
        </div>
      ) : (
        <div className="quiz-section">
          <h2>Matchup</h2>
          <div className="matchup">
            <button onClick={() => handleChoice(currentMatchup.left)}>{currentMatchup.left}</button>
            <span>vs</span>
            <button onClick={() => handleChoice(currentMatchup.right)}>{currentMatchup.right}</button>
          </div>
        </div>
      )}
      <footer>
        <p>Made with ❤️ by a fellow cat lover</p>
      </footer>
    </div>
  );
}

export default App;
