import React, { useState, useEffect } from 'react';
import { supabase } from '../../supabase/supabaseClient';
import './TournamentSetup.css';

function TournamentSetup({ onStart, existingRatings = [] }) {
  const [availableNames, setAvailableNames] = useState([]);
  const [selectedNames, setSelectedNames] = useState([]);
  const [customNames, setCustomNames] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [bracketSize, setBracketSize] = useState(8);
  const [validationError, setValidationError] = useState('');

  const BRACKET_SIZES = [2, 4, 8, 16, 32, 64];
  const MIN_NAMES = 2;

  useEffect(() => {
    fetchNames();
  }, []);

  const validateName = (name) => {
    if (!name || typeof name !== 'string') return false;
    const trimmedName = name.trim();
    if (trimmedName.length < 2) return false;
    if (trimmedName.length > 50) return false;
    // Basic regex to allow letters, numbers, spaces, and common special characters
    return /^[a-zA-Z0-9\s\-']+$/.test(trimmedName);
  };

  const fetchNames = async () => {
    try {
      const { data, error } = await supabase
        .from('name_options')
        .select('name')
        .order('name');

      if (error) throw error;

      const names = data.map(item => item.name);
      setAvailableNames(names);
      setIsLoading(false);
    } catch (err) {
      console.error('Error fetching names:', err);
      setError('Failed to load names. Please try again later.');
      setIsLoading(false);
    }
  };

  const handleRandomSelect = () => {
    if (availableNames.length < MIN_NAMES) {
      setValidationError('Not enough names available for selection');
      return;
    }
    const shuffled = [...availableNames].sort(() => Math.random() - 0.5);
    setSelectedNames(shuffled.slice(0, bracketSize));
    setValidationError('');
  };

  const toggleName = (name) => {
    setSelectedNames(prev => {
      if (prev.includes(name)) {
        const newSelection = prev.filter(n => n !== name);
        setValidationError(newSelection.length < MIN_NAMES ? 
          'At least 2 names are required' : '');
        return newSelection;
      }
      if (prev.length >= bracketSize) {
        setValidationError(`Maximum ${bracketSize} names allowed`);
        return prev;
      }
      setValidationError('');
      return [...prev, name];
    });
  };

  const handleSelectAll = () => {
    if (availableNames.length < MIN_NAMES) {
      setValidationError('Not enough names available');
      return;
    }
    setSelectedNames(availableNames.slice(0, bracketSize));
    setValidationError('');
  };

  const handleCustomNamesSubmit = (e) => {
    e.preventDefault();
    const newNames = customNames
      .split('\n')
      .map(name => name.trim())
      .filter(name => {
        const isValid = validateName(name);
        if (!isValid) {
          setValidationError(`Invalid name format: ${name}`);
        }
        return isValid;
      });

    if (newNames.length === 0) {
      setValidationError('No valid names entered');
      return;
    }

    // Check for duplicates
    const existingNames = new Set(selectedNames);
    const duplicates = newNames.filter(name => existingNames.has(name));
    if (duplicates.length > 0) {
      setValidationError(`Duplicate names not allowed: ${duplicates.join(', ')}`);
      return;
    }

    // Add new names up to bracket size
    const availableSlots = bracketSize - selectedNames.length;
    const namesToAdd = newNames.slice(0, availableSlots);

    setSelectedNames(prev => {
      const newSelection = [...new Set([...prev, ...namesToAdd])];
      if (newSelection.length < MIN_NAMES) {
        setValidationError('At least 2 names are required');
      } else {
        setValidationError('');
      }
      return newSelection;
    });
    setCustomNames('');
  };

  const handleStart = () => {
    if (selectedNames.length < MIN_NAMES) {
      setValidationError('Please select at least 2 names to start the tournament.');
      return;
    }
    if (selectedNames.length > bracketSize) {
      setValidationError(`Maximum ${bracketSize} names allowed`);
      return;
    }
    setValidationError('');
    onStart(selectedNames);
  };

  if (isLoading) return <div className="loading">Loading names...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="tournament-setup">
      <h2>Name Selection</h2>

      <div className="bracket-size-selector">
        <h3>Select Bracket Size</h3>
        <div className="size-buttons">
          {BRACKET_SIZES.map(size => (
            <button
              key={size}
              onClick={() => {
                setBracketSize(size);
                setSelectedNames(prev => prev.slice(0, size));
                setValidationError(
                  selectedNames.length < MIN_NAMES ? 'At least 2 names are required' : ''
                );
              }}
              className={`size-button ${bracketSize === size ? 'active' : ''}`}
            >
              {size} Names
            </button>
          ))}
        </div>
      </div>

      {validationError && (
        <div className="validation-error">
          {validationError}
        </div>
      )}

      <div className="selection-controls">
        <button onClick={handleSelectAll} className="control-button">
          Select First {bracketSize} Names
        </button>
        <button onClick={handleRandomSelect} className="control-button">
          Random {bracketSize} Names
        </button>
        <button 
          onClick={() => {
            setSelectedNames([]);
            setValidationError('At least 2 names are required');
          }} 
          className="control-button clear"
        >
          Clear Selection
        </button>
      </div>

      <div className="name-count">
        Selected: {selectedNames.length} / {bracketSize} names
        {selectedNames.length > 0 && selectedNames.length < MIN_NAMES && (
          <span className="warning"> (Select at least {MIN_NAMES} names)</span>
        )}
      </div>

      <div className="names-grid">
        {availableNames.map(name => (
          <div
            key={name}
            onClick={() => toggleName(name)}
            className={`name-card ${selectedNames.includes(name) ? 'selected' : ''} ${
              selectedNames.length >= bracketSize && !selectedNames.includes(name) ? 'disabled' : ''
            }`}
          >
            <span className="name-text">{name}</span>
            {selectedNames.includes(name) && (
              <span className="check-mark">âœ“</span>
            )}
          </div>
        ))}
      </div>

      <div className="custom-names-section">
        <h3>Add Custom Names</h3>
        <p className="custom-names-info">
          {bracketSize - selectedNames.length} slots remaining
        </p>
        <form onSubmit={handleCustomNamesSubmit}>
          <textarea
            value={customNames}
            onChange={(e) => setCustomNames(e.target.value)}
            placeholder={`Enter custom names (one per line, up to ${bracketSize - selectedNames.length} more)`}
            rows={4}
            disabled={selectedNames.length >= bracketSize}
          />
          <button 
            type="submit" 
            className="add-custom-button"
            disabled={selectedNames.length >= bracketSize}
          >
            Add Custom Names
          </button>
        </form>
      </div>

      <div className="start-section">
        <button
          onClick={handleStart}
          className="start-button"
          disabled={selectedNames.length < MIN_NAMES}
        >
          Start Tournament with {selectedNames.length} Names
        </button>
      </div>
    </div>
  );
}

export default TournamentSetup;
