/**
 * @module NameCard
 * @description A reusable card component for displaying name options
 * with consistent styling and behavior across the application.
 */

import React from 'react';
import './NameCard.css';

function NameCard({
  name,
  description,
  isSelected,
  onClick,
  disabled = false,
  shortcutHint,
  className = '',
  size = 'medium',
  showFullDescription = false
}) {
  // Truncate description for mobile
  const truncateDescription = (desc) => {
    if (!desc) return '';
    return desc.length > 60 ? `${desc.substring(0, 60)}...` : desc;
  };

  return (
    <div 
      className={`name-card ${isSelected ? 'selected' : ''} ${size} ${className}`}
      onClick={() => !disabled && onClick?.()}
      role="button"
      tabIndex={disabled ? -1 : 0}
      title={shortcutHint}
    >
      <h3>{name}</h3>
      {description && (
        <p className="name-description">
          {showFullDescription ? description : truncateDescription(description)}
        </p>
      )}
      {isSelected && <span className="check-mark">âœ“</span>}
    </div>
  );
}

export default NameCard;
