import React, { useState, useEffect } from 'react';
import useSupabaseStorage from '../../supabase/useSupabaseStorage';
import { supabase } from '../../supabase/supabaseClient';

function TournamentSetup({ onStart, userName }) {
  const [customNames, setCustomNames] = useState('');
  const [selectedSize, setSelectedSize] = useState('medium');
  const [useDefaultNames, setUseDefaultNames] = useState(true);
  const [availableNames, setAvailableNames] = useState([]);
  const [selectedNames, setSelectedNames] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchAvailableNames();
  }, []);

  const fetchAvailableNames = async () => {
    try {
      const { data, error } = await supabase
        .from('name_options')
        .select('name')
        .order('name');

      if (error) throw error;

      setAvailableNames(data.map(item => item.name));
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  const handleNameToggle = (name) => {
    setSelectedNames(prev => {
      if (prev.includes(name)) {
        return prev.filter(n => n !== name);
      } else {
        return [...prev, name];
      }
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    let namesToUse = [];
    if (useDefaultNames) {
      // Use selected names from available options
      const count = getSizeCount(selectedSize);
      if (selectedNames.length < count) {
        alert(`Please select at least ${count} names for a ${selectedSize} tournament.`);
        return;
      }
      namesToUse = shuffleArray([...selectedNames]);
    } else {
      // Use custom names
      namesToUse = customNames
        .split(',')
        .map(name => name.trim())
        .filter(name => name.length > 0);

      // Validate we have enough names
      const count = getSizeCount(selectedSize);
      if (namesToUse.length < count) {
        alert(`Please enter at least ${count} names for a ${selectedSize} tournament.`);
        return;
      }
    }

    onStart(namesToUse);
  };

  const getSizeCount = (size) => {
    switch (size) {
      case 'small': return 8;
      case 'medium': return 12;
      case 'large': return 16;
      default: return 12;
    }
  };

  const shuffleArray = (array) => {
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
  };

  if (loading) return <div className="loading">Loading available names...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="tournament-setup">
      <h2>Tournament Setup</h2>

      <form onSubmit={handleSubmit} className="setup-form">
        <div className="form-section">
          <h3>Tournament Size</h3>
          <div className="size-options">
            <button
              type="button"
              className={`size-button ${selectedSize === 'small' ? 'active' : ''}`}
              onClick={() => setSelectedSize('small')}
            >
              Small (8 names)
            </button>
            <button
              type="button"
              className={`size-button ${selectedSize === 'medium' ? 'active' : ''}`}
              onClick={() => setSelectedSize('medium')}
            >
              Medium (12 names)
            </button>
            <button
              type="button"
              className={`size-button ${selectedSize === 'large' ? 'active' : ''}`}
              onClick={() => setSelectedSize('large')}
            >
              Large (16 names)
            </button>
          </div>
        </div>

        <div className="form-section">
          <h3>Name Selection</h3>
          <div className="name-source-toggle">
            <button
              type="button"
              className={`source-button ${useDefaultNames ? 'active' : ''}`}
              onClick={() => setUseDefaultNames(true)}
            >
              Choose from Available Names
            </button>
            <button
              type="button"
              className={`source-button ${!useDefaultNames ? 'active' : ''}`}
              onClick={() => setUseDefaultNames(false)}
            >
              Add Custom Names
            </button>
          </div>

          {useDefaultNames && (
            <div className="available-names">
              <p>Select at least {getSizeCount(selectedSize)} names:</p>
              <div className="names-grid">
                {availableNames.map(name => (
                  <button
                    key={name}
                    type="button"
                    className={`name-option ${selectedNames.includes(name) ? 'selected' : ''}`}
                    onClick={() => handleNameToggle(name)}
                  >
                    {name}
                  </button>
                ))}
              </div>
              <p className="selection-count">
                Selected: {selectedNames.length} / {getSizeCount(selectedSize)} required
              </p>
            </div>
          )}

          {!useDefaultNames && (
            <div className="custom-names-input">
              <p>Enter names separated by commas</p>
              <textarea
                value={customNames}
                onChange={(e) => setCustomNames(e.target.value)}
                placeholder="e.g., Whiskers, Mittens, Shadow"
                rows={4}
              />
              <p className="help-text">You need at least {getSizeCount(selectedSize)} names for the selected tournament size</p>
            </div>
          )}
        </div>

        <button 
          type="submit" 
          className="start-button"
          disabled={useDefaultNames ? selectedNames.length < getSizeCount(selectedSize) : false}
        >
          Start Tournament
        </button>
      </form>
    </div>
  );
}

export default TournamentSetup;
