/**
 * @module App
 * @description Main application component for the cat name tournament app.
 * Manages the overall application state and tournament flow, including:
 * - Name input and management
 * - Tournament progression
 * - Rating calculations
 * - Results display
 * 
 * Uses the Elo rating system for ranking and a custom sorting algorithm
 * for determining the best cat name through user preferences.
 * 
 * @component
 * @returns {JSX.Element} The complete application UI
 */

import React, { useState, useCallback, useMemo } from 'react';
import {
  ErrorBoundary,
  LoadingSpinner,
  Results,
  Tournament,
  Bracket
} from './components';
import useSupabaseStorage from './lib/supabase/useSupabaseStorage';
import useNameOptions from './lib/supabase/useNameOptions';
import { PreferenceSorter } from './utils/sortingAlgorithm';
import EloRating from './utils/EloRating';
import './index.css';

function App() {
  const [userName, setUserName] = useState('');
  const [currentPair, setCurrentPair] = useState({ left: null, right: null });
  const [isSorting, setIsSorting] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [results, setResults] = useState([]);
  const [sorter, setSorter] = useState(null);
  const [progress, setProgress] = useState(0);
  const [totalComparisons, setTotalComparisons] = useState(0);
  const [ratings, setRatings] = useSupabaseStorage('cat_names', []);
  const [newOption, setNewOption] = useState('');
  const elo = useMemo(() => new EloRating(), []);
  const [matches, setMatches] = useState([]);
  const [addingName, setAddingName] = useState(false);
  const [addNameError, setAddNameError] = useState(null);

  // Replace nameOptions state with useNameOptions hook
  const { 
    nameOptions, 
    loading: optionsLoading, 
    error: optionsError,
    addNameOption,
    removeNameOption 
  } = useNameOptions();

  const handleChoice = useCallback((choice) => {
    if (!window.resolveComparison) {
      return;
    }

    const {left, right} = currentPair;
    const leftCat = ratings.find(cat => cat.name === left) || { name: left, rating: elo.defaultRating };
    const rightCat = ratings.find(cat => cat.name === right) || { name: right, rating: elo.defaultRating };

    const newRatings = elo.calculateNewRatings(leftCat.rating, rightCat.rating, choice === 'left' ? -1 : choice === 'right' ? 1 : choice === 'both' ? 0 : 2);

    const updatedRatings = ratings.filter(cat => cat.name !== left && cat.name !== right);
    updatedRatings.push(
      { name: left, rating: newRatings.newRatingA },
      { name: right, rating: newRatings.newRatingB }
    );

    setRatings(updatedRatings);

    setMatches(prev => [...prev, {
      id: prev.length + 1,
      name1: left,
      name2: right,
      winner: choice === 'left' ? -1 : choice === 'right' ? 1 : choice === 'both' ? 0 : 2
    }]);

    window.resolveComparison(choice === 'left' ? -1 : choice === 'right' ? 1 : choice === 'both' ? 0 : 2);
  }, [currentPair, ratings, elo]);

  const addNewOption = async () => {
    if (newOption.trim() === '') {
      return;
    }

    setAddingName(true);
    setAddNameError(null);

    try {
      await addNameOption(newOption.trim());
      const newCat = { name: newOption.trim(), rating: elo.defaultRating };
      setRatings(prev => [...prev, newCat]);
      setNewOption('');
    } catch (error) {
      console.error('Error adding name:', error);
      setAddNameError('Failed to add name. Please try again.');
    } finally {
      setAddingName(false);
    }
  };

  const handleStart = async () => {
    if (userName.trim() === '') {
      alert('Please enter your name to start the quiz.');
      return;
    }
    if (nameOptions.length < 2) {
      alert('Not enough options to start sorting.');
      return;
    }
    const newSorter = new PreferenceSorter(nameOptions);
    setSorter(newSorter);
    setIsSorting(true);
    const n = nameOptions.length;
    const estimatedComparisons = Math.ceil(n * Math.log2(n));
    setTotalComparisons(estimatedComparisons);
    setProgress(0);

    try {
      let comparisons = 0;
      const sortedResults = await newSorter.sort(async (a, b) => {
        setCurrentPair({ left: a, right: b });
        comparisons++;
        setProgress(Math.floor((comparisons / estimatedComparisons) * 100));
        return new Promise((resolve) => {
          window.resolveComparison = resolve;
        });
      });
      setResults(sortedResults);
      setShowResults(true);
      setIsSorting(false);
    } catch (error) {
      console.error('Sorting error:', error);
    }
  };

  const handleRedo = () => {
    setCurrentPair({ left: null, right: null });
    setIsSorting(false);
    setShowResults(false);
    setResults([]);
    setSorter(null);
    setMatches([]);
  };

  const sortedResults = showResults ? 
    results.map(name => {
      const cat = ratings.find(r => r.name === name) || { name, rating: elo.defaultRating };
      return cat;
    }).sort((a, b) => b.rating - a.rating) : [];

  if (optionsLoading) return <LoadingSpinner />;
  if (optionsError) return <div>Error loading name options: {optionsError.message}</div>;

  return (
    <ErrorBoundary>
      <div className="App">
        <a href="#main" className="skip-link">
          Skip to main content
        </a>
        <header className="App-header">
          <img 
            src="/images/cat.gif" 
            alt="Cute cat animation" 
            className="cat-gif"
          />
          <h1>Help Me Name My Cat!</h1>
          <p>Welcome to the ultimate cat name sorter! Help decide the perfect name through fun matchups.</p>
        </header>

        <main id="main">
          {!isSorting && !showResults && (
            <section className="setup-section">
              <div className="user-input">
                <label htmlFor="userName">Your Name:</label>
                <input 
                  type="text" 
                  id="userName" 
                  value={userName} 
                  onChange={(e) => setUserName(e.target.value)} 
                  placeholder="Enter your name" 
                  required 
                />
              </div>

              <div className="options-management">
                <h2>Name Options</h2>
                <div className="add-option">
                  <input
                    type="text"
                    value={newOption}
                    onChange={(e) => setNewOption(e.target.value)}
                    placeholder="Add a new name option"
                    disabled={addingName}
                  />
                  <button 
                    onClick={addNewOption}
                    disabled={addingName || !newOption.trim()}
                  >
                    {addingName ? 'Adding...' : 'Add Name'}
                  </button>
                </div>
                <div className="options-list">
                  {nameOptions.map((option, index) => (
                    <div key={index} className="option-item">
                      {option}
                      <button 
                        onClick={() => removeNameOption(option)}
                        className="remove-button"
                        aria-label={`Remove ${option}`}
                      >
                        Ã—
                      </button>
                    </div>
                  ))}
                </div>
              </div>

              <button 
                className="primary-button"
                onClick={handleStart}
                disabled={nameOptions.length < 2 || userName.trim() === ''}
              >
                Start Sorting!
              </button>
            </section>
          )}

          {isSorting && (
            <Tournament
              currentMatch={currentPair}
              onVote={handleChoice}
              roundNumber={1}
              totalMatches={totalComparisons}
              currentMatchNumber={Math.floor((progress / 100) * totalComparisons)}
            />
          )}

          {showResults && (
            <Results 
              results={sortedResults}
              onRedo={handleRedo}
              matches={matches}
            />
          )}
        </main>
      </div>
    </ErrorBoundary>
  );
}

export default App;
