/**
 * @module App
 * @description Main application component for the cat name tournament app.
 * Manages the overall application state and tournament flow, including:
 * - Name input and management
 * - Tournament progression
 * - Rating calculations
 * - Results display
 * 
 * Uses the Elo rating system for ranking and a custom sorting algorithm
 * for determining the best cat name through user preferences.
 * 
 * @component
 * @returns {JSX.Element} The complete application UI
 */

import React, { useState } from 'react';
import { 
  Tournament, 
  Results, 
  ErrorBoundary,
  Login,
  Profile,
  TournamentSetup
} from './components';
import useUserSession from './hooks/useUserSession';
import useSupabaseStorage from './supabase/useSupabaseStorage';
import { supabase } from './supabase/supabaseClient';

function App() {
  const { userName, isLoggedIn, login, logout, session } = useUserSession();
  const [ratings, setRatings] = useState({});
  const [view, setView] = useState('tournament');
  const [tournamentComplete, setTournamentComplete] = useState(false);
  const [tournamentNames, setTournamentNames] = useState(null);

  console.log('App - Current ratings:', ratings);
  console.log('App - Tournament names:', tournamentNames);

  const handleTournamentComplete = (finalRatings) => {
    // Merge new ratings with existing ones, preserving wins/losses and name_id
    const updatedRatings = { ...ratings };
    finalRatings.forEach(({ name, rating }) => {
      const existingRating = typeof updatedRatings[name] === 'object' 
        ? updatedRatings[name] 
        : { rating: updatedRatings[name] || 1500, wins: 0, losses: 0 };

      // If rating improved, count as a win, otherwise a loss
      const isImprovement = rating > (existingRating.rating || 1500);

      // Generate a UUID if one doesn't exist
      const nameId = existingRating.name_id || crypto.randomUUID();

      updatedRatings[name] = {
        name_id: nameId,
        rating: Math.round(rating),
        wins: (existingRating.wins || 0) + (isImprovement ? 1 : 0),
        losses: (existingRating.losses || 0) + (isImprovement ? 0 : 1)
      };
    });

    // Update the ratings state
    setRatings(updatedRatings);
    setTournamentComplete(true);

    // Persist to database
    const updateRatings = async () => {
      try {
        // First try to delete existing records that have valid UUIDs
        const existingIds = Object.values(updatedRatings)
          .map(data => data.name_id)
          .filter(id => id && /^[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i.test(id));

        if (existingIds.length > 0) {
          const { error: deleteError } = await supabase
            .from('cat_name_ratings')
            .delete()
            .in('name_id', existingIds);

          if (deleteError) {
            console.warn('Error deleting existing ratings:', deleteError);
          }
        }

        // Then insert new records
        const { error: insertError } = await supabase
          .from('cat_name_ratings')
          .insert(
            Object.entries(updatedRatings).map(([name, data]) => ({
              name_id: data.name_id,
              name: name,  // Store the actual name as well
              rating: data.rating,
              wins: data.wins,
              losses: data.losses,
              updated_at: new Date().toISOString()
            }))
          );

        if (insertError) throw insertError;
      } catch (error) {
        console.error('Error updating tournament ratings:', error);
      }
    };

    // Execute the update
    updateRatings();
  };

  const handleStartNewTournament = () => {
    setTournamentComplete(false);
    setTournamentNames(null);
    setView('tournament');
  };

  const handleTournamentSetup = (names) => {
    console.log('App - Setting up tournament with names:', names);
    setTournamentNames(names);
  };

  const handleUpdateRatings = (adjustedRatings) => {
    // Convert array to object format and merge with existing ratings
    const updatedRatings = { ...ratings };
    adjustedRatings.forEach(({ name, rating, wins, losses }) => {
      const existingRating = typeof updatedRatings[name] === 'object'
        ? updatedRatings[name]
        : { rating: updatedRatings[name] || 1500, wins: 0, losses: 0 };

      updatedRatings[name] = {
        name_id: existingRating.name_id,
        rating: Math.round(rating),
        wins: wins || existingRating.wins || 0,
        losses: losses || existingRating.losses || 0
      };
    });

    setRatings(updatedRatings);

    // Update ratings in Supabase
    const updateRatings = async () => {
      try {
        // Get the current session
        const { data: { session }, error: sessionError } = await supabase.auth.getSession();
        if (sessionError) throw sessionError;
        if (!session) throw new Error('No active session');

        // First, get name_ids from name_options table
        const { data: nameOptions, error: nameError } = await supabase
          .from('name_options')
          .select('id, name')
          .in('name', Object.keys(updatedRatings));

        if (nameError) throw nameError;
        if (!nameOptions?.length) {
          console.warn('No matching name options found');
          return;
        }

        // Create a map of name to name_id
        const nameToIdMap = nameOptions.reduce((acc, { id, name }) => {
          acc[name] = id;
          return acc;
        }, {});

        // Prepare records with proper name_ids
        const recordsToUpsert = Object.entries(updatedRatings)
          .map(([name, data]) => {
            const name_id = nameToIdMap[name];
            if (!name_id) {
              console.warn(`No name_id found for ${name}`);
              return null;
            }
            return {
              user_name: session.user.id,
              name_id: name_id,
              rating: data.rating,
              wins: data.wins || 0,
              losses: data.losses || 0,
              updated_at: new Date().toISOString()
            };
          })
          .filter(Boolean); // Remove null entries

        if (!recordsToUpsert.length) {
          console.warn('No valid records to update');
          return;
        }

        // Use upsert operation (insert or update based on primary key)
        const { error: upsertError } = await supabase
          .from('cat_name_ratings')
          .upsert(recordsToUpsert, {
            onConflict: 'user_name,name_id',
            returning: 'minimal' // Don't need to return the updated records
          });

        if (upsertError) throw upsertError;
      } catch (error) {
        console.error('Error updating ratings:', error);
        // Don't throw here, just log the error
        // This prevents the unhandled promise rejection
      }
    };

    // Execute the update
    updateRatings();
  };

  const handleLogout = async () => {
    logout();
  };

  if (!isLoggedIn) {
    return <Login onLogin={login} />;
  }

  const renderMainContent = () => {
    if (view === 'profile') {
      return (
        <Profile 
          userName={userName}
          onStartNewTournament={handleStartNewTournament}
          ratings={ratings}
          onUpdateRatings={handleUpdateRatings}
        />
      );
    }

    if (tournamentComplete) {
      return (
        <Results 
          ratings={ratings}
          onStartNew={handleStartNewTournament}
          userName={userName}
          onUpdateRatings={handleUpdateRatings}
          currentTournamentNames={tournamentNames}
        />
      );
    }

    if (!tournamentNames) {
      return (
        <TournamentSetup 
          onStart={handleTournamentSetup}
          userName={userName}
          existingRatings={ratings}
        />
      );
    }

    return (
      <Tournament 
        onComplete={handleTournamentComplete}
        existingRatings={ratings}
        names={tournamentNames}
        userName={userName}
      />
    );
  };

  return (
    <div className="app">
      <header>
        <div 
          className="header-background"
          style={{ 
            backgroundImage: `url(${process.env.PUBLIC_URL}/images/cat.gif)` 
          }}
        ></div>
        <img src={`${process.env.PUBLIC_URL}/images/cat.gif`} alt="Cat animation" className="header-image" />
        <h1>Cat Name Picker</h1>
        <div className="user-controls">
          <div className="nav-menu">
            <button 
              onClick={() => setView('tournament')}
              className={view === 'tournament' ? 'active' : ''}
            >
              Tournament
            </button>
            <button 
              onClick={() => setView('profile')}
              className={view === 'profile' ? 'active' : ''}
            >
              My Profile
            </button>
          </div>
          <span className="user-welcome">Welcome, {userName}!</span>
          <button onClick={handleLogout} className="logout-button">
            Logout
          </button>
        </div>
      </header>

      <main>
        <ErrorBoundary>
          {renderMainContent()}
        </ErrorBoundary>
      </main>
    </div>
  );
}

export default App;
