import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [userName, setUserName] = useState('');
  const [options, setOptions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [results, setResults] = useState([]);
  const [isSorting, setIsSorting] = useState(false);
  const [showResults, setShowResults] = useState(false);

  useEffect(() => {
    fetch('options.txt')
      .then(response => response.text())
      .then(data => {
        const optionsArray = data.split('\n').filter(option => option.trim() !== '');
        setOptions(optionsArray);
      })
      .catch(error => console.error('Error loading options:', error));
  }, []);

  const handleStart = () => {
    if (userName.trim() === '') {
      alert('Please enter your name to start the quiz.');
      return;
    }
    setIsSorting(true);
  };

  const handleChoice = (choice) => {
    setResults(prevResults => [...prevResults, { choice, option: options[currentIndex] }]);
    if (currentIndex < options.length - 1) {
      setCurrentIndex(currentIndex + 1);
    } else {
      setIsSorting(false);
      setShowResults(true);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Help Me Name My Cat!</h1>
        <p>Welcome to the ultimate cat name sorter! Please help me decide on the perfect name by choosing your favorite option in each matchup.</p>
      </header>
      {!isSorting && !showResults && (
        <div className="user-input">
          <label htmlFor="userName">Enter your name:</label>
          <input type="text" id="userName" value={userName} onChange={(e) => setUserName(e.target.value)} placeholder="Your Name" required />
          <button onClick={handleStart}>Start Sorting</button>
        </div>
      )}
      {isSorting && (
        <div className="quiz-section">
          <h2>Matchup</h2>
          <div className="matchup">
            <button onClick={() => handleChoice('left')}>{options[currentIndex]}</button>
            <button onClick={() => handleChoice('both')}>I like both</button>
            <button onClick={() => handleChoice('none')}>No Opinion</button>
            {currentIndex + 1 < options.length && <button onClick={() => handleChoice('right')}>{options[currentIndex + 1]}</button>}
          </div>
        </div>
      )}
      {showResults && (
        <div className="results-section">
          <h2>Results</h2>
          <ul>
            {results.map((result, index) => (
              <li key={index}>{result.option} - {result.choice}</li>
            ))}
          </ul>
        </div>
      )}
      <footer>
        <p>Made with ❤️ by a fellow cat lover</p>
      </footer>
    </div>
  );
}

export default App;
