/**
 * @module App
 * @description Main application component for the cat name tournament app.
 * Manages the overall application state and tournament flow, including:
 * - Name input and management
 * - Tournament progression
 * - Rating calculations
 * - Results display
 * 
 * Uses the Elo rating system for ranking and a custom sorting algorithm
 * for determining the best cat name through user preferences.
 * 
 * @component
 * @returns {JSX.Element} The complete application UI
 */

import React, { useState } from 'react';
import { 
  Tournament, 
  Results, 
  ErrorBoundary,
  Login,
  Profile,
  TournamentSetup
} from './components';
import useUserSession from './hooks/useUserSession';
import useSupabaseStorage from './supabase/useSupabaseStorage';
import { supabase } from './supabase/supabaseClient';

function App() {
  const { userName, isLoggedIn, login, logout, session } = useUserSession();
  const [ratings, setRatings] = useState({});
  const [view, setView] = useState('tournament');
  const [tournamentComplete, setTournamentComplete] = useState(false);
  const [tournamentNames, setTournamentNames] = useState(null);

  console.log('App - Current ratings:', ratings);
  console.log('App - Tournament names:', tournamentNames);

  const handleTournamentComplete = (finalRatings) => {
    console.log('App - Tournament complete with ratings:', finalRatings);

    // Merge new ratings with existing ones, preserving wins/losses
    const updatedRatings = { ...ratings };
    finalRatings.forEach(({ name, rating }) => {
      const existingRating = updatedRatings[name] || { rating: 1500, wins: 0, losses: 0 };

      // If rating improved, count as a win, otherwise a loss
      const isImprovement = rating > (existingRating.rating || 1500);
      updatedRatings[name] = {
        rating,
        wins: (existingRating.wins || 0) + (isImprovement ? 1 : 0),
        losses: (existingRating.losses || 0) + (isImprovement ? 0 : 1)
      };
    });

    console.log('App - Updated ratings after merge:', updatedRatings);
    setRatings(updatedRatings);
    setTournamentComplete(true);
  };

  const handleStartNewTournament = () => {
    setTournamentComplete(false);
    setTournamentNames(null);
    setView('tournament');
  };

  const handleTournamentSetup = (names) => {
    console.log('App - Setting up tournament with names:', names);
    setTournamentNames(names);
  };

  const handleUpdateRatings = (adjustedRatings) => {
    console.log('App - Updating ratings with:', adjustedRatings);

    // Ensure adjustedRatings is an array
    const ratingsArray = Array.isArray(adjustedRatings) 
      ? adjustedRatings 
      : Object.entries(adjustedRatings).map(([name, rating]) => ({ name, rating }));

    // Convert array to object format and merge with existing ratings
    const updatedRatings = { ...ratings };
    ratingsArray.forEach(({ name, rating }) => {
      updatedRatings[name] = Math.round(rating);
    });

    console.log('App - Final updated ratings:', updatedRatings);
    setRatings(updatedRatings);

    // Update ratings in Supabase
    const updateRatings = async () => {
      try {
        // First, get name_ids for all names
        const { data: nameOptions, error: nameError } = await supabase
          .from('name_options')
          .select('id, name')
          .in('name', ratingsArray.map(r => r.name));

        if (nameError) throw nameError;

        // Create a map of name to id
        const nameToIdMap = nameOptions.reduce((acc, opt) => {
          acc[opt.name] = opt.id;
          return acc;
        }, {});

        // Prepare the data with the correct structure
        const ratingsData = ratingsArray.map(({ name, rating }) => ({
          user_name: userName,
          name_id: nameToIdMap[name],
          rating: Math.round(rating),
          updated_at: new Date().toISOString()
        })).filter(data => data.name_id); // Only include ratings where we found a name_id

        // Perform the upsert operation
        const { error: upsertError } = await supabase
          .from('cat_name_ratings')
          .upsert(ratingsData, {
            onConflict: 'user_name,name_id',
            returning: true
          });

        if (upsertError) {
          console.error('Upsert error:', upsertError);
          throw upsertError;
        }

        console.log('Ratings updated successfully');
      } catch (err) {
        console.error('Error updating ratings:', err);
        console.warn('Failed to save to database, but local state is updated');
      }
    };

    // Execute the update
    updateRatings();
  };

  const handleLogout = async () => {
    logout();
  };

  if (!isLoggedIn) {
    return <Login onLogin={login} />;
  }

  const renderMainContent = () => {
    if (view === 'profile') {
      return (
        <Profile 
          userName={userName}
          onStartNewTournament={handleStartNewTournament}
          ratings={ratings}
          onUpdateRatings={handleUpdateRatings}
        />
      );
    }

    if (tournamentComplete) {
      return (
        <Results 
          ratings={ratings}
          onStartNew={handleStartNewTournament}
          userName={userName}
          onUpdateRatings={handleUpdateRatings}
          currentTournamentNames={tournamentNames}
        />
      );
    }

    if (!tournamentNames) {
      return (
        <TournamentSetup 
          onStart={handleTournamentSetup}
          userName={userName}
          existingRatings={ratings}
        />
      );
    }

    return (
      <Tournament 
        onComplete={handleTournamentComplete}
        existingRatings={ratings}
        names={tournamentNames}
        userName={userName}
      />
    );
  };

  return (
    <div className="app">
      <header>
        <div 
          className="header-background"
          style={{ 
            backgroundImage: `url(${process.env.PUBLIC_URL}/images/cat.gif)` 
          }}
        ></div>
        <img src={`${process.env.PUBLIC_URL}/images/cat.gif`} alt="Cat animation" className="header-image" />
        <h1>Cat Name Picker</h1>
        <div className="user-controls">
          <div className="nav-menu">
            <button 
              onClick={() => setView('tournament')}
              className={view === 'tournament' ? 'active' : ''}
            >
              Tournament
            </button>
            <button 
              onClick={() => setView('profile')}
              className={view === 'profile' ? 'active' : ''}
            >
              My Profile
            </button>
          </div>
          <span className="user-welcome">Welcome, {userName}!</span>
          <button onClick={handleLogout} className="logout-button">
            Logout
          </button>
        </div>
      </header>

      <main>
        <ErrorBoundary>
          {renderMainContent()}
        </ErrorBoundary>
      </main>
    </div>
  );
}

export default App;
