/**
 * @module App
 * @description Main application component for the cat name tournament app.
 * Manages the overall application state and tournament flow, including:
 * - Name input and management
 * - Tournament progression
 * - Rating calculations
 * - Results display
 * 
 * Uses the Elo rating system for ranking and a custom sorting algorithm
 * for determining the best cat name through user preferences.
 * 
 * @component
 * @returns {JSX.Element} The complete application UI
 */

import React, { useState, useEffect, useCallback, useMemo } from 'react';
import {
  ErrorBoundary,
  LoadingSpinner,
  Results,
  Tournament,
  Bracket
} from './components';
import useLocalStorage from './hooks/useLocalStorage';
import { PreferenceSorter } from './utils/sortingAlgorithm';
import EloRating from './utils/EloRating';
import SupabaseTest from './components/SupabaseTest';
import './index.css';

function App() {
  const [userName, setUserName] = useState('');
  const [nameOptions, setNameOptions] = useState([]);
  const [currentPair, setCurrentPair] = useState({ left: null, right: null });
  const [isSorting, setIsSorting] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [results, setResults] = useState([]);
  const [sorter, setSorter] = useState(null);
  const [progress, setProgress] = useState(0);
  const [totalComparisons, setTotalComparisons] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [ratings, setRatings] = useLocalStorage('catNameRatings', {});
  const [newOption, setNewOption] = useState('');
  const elo = useMemo(() => new EloRating(), []);
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    const fetchOptions = async () => {
      setIsLoading(true);
      try {
        const response = await fetch('/options.txt');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.text();
        const optionsArray = data.split('\n').filter(option => option.trim() !== '');
        if (optionsArray.length > 1) {
          setNameOptions(shuffleArray(optionsArray));
        } else {
          console.error('Insufficient options to perform sorting.');
        }
        setIsLoading(false);
      } catch (error) {
        console.error('Error loading options:', error);
        setIsLoading(false);
      }
    };
    fetchOptions();
  }, []);

  const addNewOption = () => {
    if (newOption.trim() === '') {
      return;
    }
    setNameOptions(prev => [...prev, newOption.trim()]);
    setRatings(prev => ({...prev, [newOption.trim()]: elo.defaultRating}));
    setNewOption('');
  };

  const handleChoice = useCallback((choice) => {
    if (!window.resolveComparison) {
      return;
    }

    const {left, right} = currentPair;
    const ratingLeft = ratings[left] || elo.defaultRating;
    const ratingRight = ratings[right] || elo.defaultRating;

    const newRatings = elo.calculateNewRatings(ratingLeft, ratingRight, choice === 'left' ? -1 : choice === 'right' ? 1 : choice === 'both' ? 0 : 2);
    setRatings(prev => ({
      ...prev,
      [left]: newRatings.newRatingA,
      [right]: newRatings.newRatingB
    }));

    setMatches(prev => [...prev, {
      id: prev.length + 1,
      name1: left,
      name2: right,
      winner: choice === 'left' ? -1 : choice === 'right' ? 1 : choice === 'both' ? 0 : 2
    }]);

    window.resolveComparison(choice === 'left' ? -1 : choice === 'right' ? 1 : choice === 'both' ? 0 : 2);
  }, [currentPair, ratings, elo]);

  useEffect(() => {
    const handleKeyPress = (event) => {
      if (!isSorting) {
        return;
      }

      switch(event.key) {
        case 'ArrowLeft':
          handleChoice('left');
          break;
        case 'ArrowRight':
          handleChoice('right');
          break;
        case 'b':
        case 'B':
          handleChoice('both');
          break;
        case 'n':
        case 'N':
          handleChoice('none');
          break;
        default:
          break;
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [isSorting, handleChoice]);

  useEffect(() => {
    if (isSorting) {
      const handleTabKey = (e) => {
        if (e.key === 'Tab') {
          const focusableElements = document.querySelectorAll(
            'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])'
          );
          const firstFocusableElement = focusableElements[0];
          const lastFocusableElement = focusableElements[focusableElements.length - 1];

          if (e.shiftKey && document.activeElement === firstFocusableElement) {
            e.preventDefault();
            lastFocusableElement.focus();
          } else if (!e.shiftKey && document.activeElement === lastFocusableElement) {
            e.preventDefault();
            firstFocusableElement.focus();
          }
        }
      };

      document.addEventListener('keydown', handleTabKey);
      return () => document.removeEventListener('keydown', handleTabKey);
    }
  }, [isSorting]);

  const handleStart = async () => {
    if (userName.trim() === '') {
      alert('Please enter your name to start the quiz.');
      return;
    }
    if (nameOptions.length < 2) {
      alert('Not enough options to start sorting.');
      return;
    }
    const newSorter = new PreferenceSorter(nameOptions);
    setSorter(newSorter);
    setIsSorting(true);
    const n = nameOptions.length;
    const estimatedComparisons = Math.ceil(n * Math.log2(n));
    setTotalComparisons(estimatedComparisons);
    setProgress(0);

    try {
      let comparisons = 0;
      const sortedResults = await newSorter.sort(async (a, b) => {
        setCurrentPair({ left: a, right: b });
        comparisons++;
        setProgress(Math.floor((comparisons / estimatedComparisons) * 100));
        return new Promise((resolve) => {
          window.resolveComparison = resolve;
        });
      });
      setResults(sortedResults);
      setShowResults(true);
      setIsSorting(false);
    } catch (error) {
      console.error('Sorting error:', error);
    }
  };

  const shuffleArray = (array) => {
    // Fisher-Yates shuffle algorithm
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
  };

  const handleRedo = () => {
    setCurrentPair({ left: null, right: null });
    setIsSorting(false);
    setShowResults(false);
    setResults([]);
    setSorter(null);
    setMatches([]);
    setNameOptions(shuffleArray([...nameOptions]));
  };

  const sortedResults = showResults ? 
    [...results].sort((a, b) => (ratings[b] || 0) - (ratings[a] || 0)) : [];

  return (
    <ErrorBoundary>
      <div className="App">
        <a href="#main" className="skip-link">
          Skip to main content
        </a>
        <header className="App-header">
          <img 
            src="/images/cat.gif" 
            alt="Cute cat animation" 
            className="cat-gif"
          />
          <h1>Help Me Name My Cat!</h1>
          <p>Welcome to the ultimate cat name sorter! Help decide the perfect name through fun matchups.</p>
        </header>

        <SupabaseTest />

        <main id="main">
          {isLoading ? (
            <LoadingSpinner />
          ) : (
            <>
              {!isSorting && !showResults && (
                <section className="setup-section">
                  <div className="user-input">
                    <label htmlFor="userName">Your Name:</label>
                    <input 
                      type="text" 
                      id="userName" 
                      value={userName} 
                      onChange={(e) => setUserName(e.target.value)} 
                      placeholder="Enter your name" 
                      required 
                    />
                  </div>

                  <div className="options-management">
                    <h2>Name Options</h2>
                    <div className="add-option">
                      <input
                        type="text"
                        value={newOption}
                        onChange={(e) => setNewOption(e.target.value)}
                        placeholder="Add a new name option"
                        onKeyPress={(e) => e.key === 'Enter' && addNewOption()}
                      />
                      <button className="primary-button" onClick={addNewOption}>
                        Add Name
                      </button>
                    </div>

                    <div className="options-list">
                      {nameOptions.map((option, index) => (
                        <div key={index} className="option-item">
                          {option}
                        </div>
                      ))}
                    </div>
                  </div>

                  {nameOptions.length >= 2 && (
                    <button 
                      className="primary-button start-button"
                      onClick={handleStart}
                    >
                      Start Sorting!
                    </button>
                  )}
                </section>
              )}

              {isSorting && currentPair.left && currentPair.right && (
                <Tournament
                  currentMatch={{
                    left: currentPair.left,
                    right: currentPair.right
                  }}
                  onVote={handleChoice}
                  roundNumber={1}
                  totalMatches={totalComparisons}
                  currentMatchNumber={Math.floor((progress / 100) * totalComparisons)}
                  onNavigate={(direction) => {
                    // Navigation can be implemented in a future update
                    console.log('Navigation:', direction);
                  }}
                />
              )}

              {showResults && (
                <>
                  <Results 
                    ratings={ratings} 
                    onRestart={handleRedo}
                  />
                  <Bracket matches={matches} />
                </>
              )}
            </>
          )}
        </main>

        <footer>
          <p>Made with ❤️ by a fellow cat lover</p>
        </footer>
      </div>
    </ErrorBoundary>
  );
}

export default App;
