import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';
import './RankingAdjustment.css';

function RankingAdjustment({ 
  rankings, 
  onSave, 
  onCancel,
  onRankingsUpdate,
  showControls = true 
}) {
  const [adjustedRankings, setAdjustedRankings] = useState(
    rankings.map((item, index) => ({
      ...item,
      id: item.id || item.name, // Fallback to name if id is not provided
      rank: item.rank || (index + 1)
    }))
  );

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const items = Array.from(adjustedRankings);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    // Update ranks based on new positions
    const updatedItems = items.map((item, index) => ({
      ...item,
      rank: index + 1,
      rating: Math.round(2000 - (index * (1000 / items.length)))
    }));

    setAdjustedRankings(updatedItems);
    if (onRankingsUpdate) {
      onRankingsUpdate(updatedItems);
    }
  };

  const handleRankChange = (itemId, newRank) => {
    const updatedRankings = adjustedRankings.map(item => {
      if (item.id === itemId) {
        return { ...item, rank: parseInt(newRank) };
      }
      return item;
    });

    // Sort by rank
    const sortedRankings = updatedRankings.sort((a, b) => a.rank - b.rank);
    setAdjustedRankings(sortedRankings);
    if (onRankingsUpdate) {
      onRankingsUpdate(sortedRankings);
    }
  };

  const handleSave = () => {
    if (onSave) {
      onSave(adjustedRankings);
    }
  };

  return (
    <div className="ranking-adjustment">
      <h3>Adjust Rankings</h3>
      <p className="instructions">
        Drag and drop items to reorder, or use the number inputs to set specific ranks.
      </p>

      <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId="rankings">
          {(provided) => (
            <div
              {...provided.droppableProps}
              ref={provided.innerRef}
              className="rankings-list"
            >
              {adjustedRankings.map((item, index) => (
                <Draggable
                  key={item.id}
                  draggableId={item.id}
                  index={index}
                >
                  {(provided, snapshot) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      className={`ranking-item ${snapshot.isDragging ? 'dragging' : ''}`}
                    >
                      <div className="item-content">
                        <span className="drag-handle">⋮⋮</span>
                        <span className="name">{item.name}</span>
                        <input
                          type="number"
                          min="1"
                          max={rankings.length}
                          value={item.rank}
                          onChange={(e) => handleRankChange(item.id, e.target.value)}
                          className="rank-input"
                        />
                      </div>
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>

      {showControls && (
        <div className="adjustment-controls">
          <button onClick={handleSave} className="save-button">
            Save Rankings
          </button>
          {onCancel && (
            <button onClick={onCancel} className="cancel-button">
              Cancel
            </button>
          )}
        </div>
      )}
    </div>
  );
}

export default RankingAdjustment;
