import React, { useState } from 'react';

const CatGallery = ({ images }) => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleImageClick = (image, index) => {
    setSelectedImage(image);
    setCurrentIndex(index);
  };

  const handleClose = () => {
    setSelectedImage(null);
  };

  const handlePrevious = (e) => {
    e.stopPropagation();
    const newIndex = (currentIndex - 1 + images.length) % images.length;
    setSelectedImage(images[newIndex]);
    setCurrentIndex(newIndex);
  };

  const handleNext = (e) => {
    e.stopPropagation();
    const newIndex = (currentIndex + 1) % images.length;
    setSelectedImage(images[newIndex]);
    setCurrentIndex(newIndex);
  };

  const handleKeyDown = (e) => {
    if (!selectedImage) return;

    switch (e.key) {
      case 'ArrowLeft':
        handlePrevious(e);
        break;
      case 'ArrowRight':
        handleNext(e);
        break;
      case 'Escape':
        handleClose();
        break;
      default:
        break;
    }
  };

  React.useEffect(() => {
    document.addEventListener('keydown', handleKeyDown);
    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, [selectedImage, currentIndex]);

  return (
    <>
      <div className="cat-gallery">
        {images.map((image, index) => (
          <div
            key={index}
            className="cat-photo-wrapper"
            onClick={() => handleImageClick(image, index)}
          >
            <img
              src={image}
              alt={`Cat photo ${index + 1}`}
              className="cat-photo"
              loading="lazy"
            />
          </div>
        ))}
      </div>

      <div
        className={`photo-overlay ${selectedImage ? 'active' : ''}`}
        onClick={handleClose}
      >
        {selectedImage && (
          <div className="enlarged-photo-container" onClick={e => e.stopPropagation()}>
            <img
              src={selectedImage}
              alt="Enlarged cat photo"
              className="enlarged-photo"
            />
            <button className="close-button" onClick={handleClose}>
              ×
            </button>
            <div className="photo-navigation">
              <button className="nav-button" onClick={handlePrevious}>
                ‹
              </button>
              <button className="nav-button" onClick={handleNext}>
                ›
              </button>
            </div>
          </div>
        )}
      </div>
    </>
  );
};

export default CatGallery;
