/**
 * @module ResultsTable
 * @description A component that displays a sorted table of cat names and their ratings.
 * Handles the sorting and formatting of ratings data.
 * 
 * @example
 * <ResultsTable 
 *   ratings={{ "Whiskers": 1450, "Mittens": 1380 }}
 * />
 * 
 * @component
 * @param {Object} props
 * @param {Object} props.ratings - Object mapping cat names to their Elo ratings
 * @returns {JSX.Element} Formatted table of cat names and their ratings
 */

import React from 'react';

function ResultsTable({ ratings }) {
  // Sort ratings by value in descending order
  const sortedRatings = Object.entries(ratings)
    .sort(([, a], [, b]) => b - a)
    .map(([name, rating]) => ({ name, rating }));

  return (
    <div className="ratings-list">
      {sortedRatings.map(({ name, rating }, index) => (
        <div key={name} className="rating-item">
          <span className="rank">{index + 1}</span>
          <span className="name">{name}</span>
          <span className="rating">Rating: {rating}</span>
        </div>
      ))}
    </div>
  );
}

export default ResultsTable;
