import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import './RankingAdjustment.css';

function RankingAdjustment({ rankings, onSave, onCancel }) {
  const [items, setItems] = useState(rankings);

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const newItems = Array.from(items);
    const [reorderedItem] = newItems.splice(result.source.index, 1);
    newItems.splice(result.destination.index, 0, reorderedItem);

    // Recalculate ratings based on new positions
    const adjustedItems = newItems.map((item, index) => ({
      ...item,
      rating: Math.max(1000, 1500 - (index * 20)) // Base rating of 1500, larger gaps between ranks
    }));

    setItems(adjustedItems);
  };

  return (
    <div className="ranking-adjustment">
      <h2>Adjust Rankings</h2>
      <p className="instructions">
        Drag and drop names to adjust their rankings. Names at the top will be rated higher.
      </p>

      <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId="rankings">
          {(provided) => (
            <div
              {...provided.droppableProps}
              ref={provided.innerRef}
              className="rankings-list"
            >
              {items.map((item, index) => (
                <Draggable 
                  key={item.name} 
                  draggableId={item.name} 
                  index={index}
                >
                  {(provided, snapshot) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      className={`ranking-item ${snapshot.isDragging ? 'dragging' : ''}`}
                    >
                      <div className="rank-number">{index + 1}</div>
                      <div className="name">{item.name}</div>
                      <div className="rating">Rating: {item.rating}</div>
                      <div className="drag-handle">
                        <span className="drag-icon">⋮⋮</span>
                      </div>
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>

      <div className="adjustment-controls">
        <button onClick={() => onSave(items)} className="save-button">
          Save Rankings
        </button>
        <button onClick={onCancel} className="cancel-button">
          Cancel
        </button>
      </div>
    </div>
  );
}

export default RankingAdjustment;
