import React, { useState, useEffect } from 'react';
import './index.css';

function App() {
  const [userName, setUserName] = useState('');
  const [nameOptions, setNameOptions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isSorting, setIsSorting] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [results, setResults] = useState([]);

  useEffect(() => {
    const fetchOptions = async () => {
      try {
        const response = await fetch('options.txt');
        if (!response.ok) throw new Error('Network response was not ok');
        const data = await response.text();
        const optionsArray = data.split('\n').filter(option => option.trim() !== '');
        if (optionsArray.length > 1) {
          setNameOptions(optionsArray);
        } else {
          console.error('Insufficient options to perform sorting.');
        }
      } catch (error) {
        console.error('Error loading options:', error);
      }
    };
    fetchOptions();
  }, []);

  const handleStart = () => {
    if (userName.trim() === '') {
      alert('Please enter your name to start the quiz.');
      return;
    }
    setIsSorting(true);
  };

  const handleChoice = (choice) => {
    // Implement choice handling logic
  };

  const handleRedo = () => {
    setCurrentIndex(0);
    setIsSorting(false);
    setShowResults(false);
    setResults([]);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Help Me Name My Cat!</h1>
        <p>Welcome to the ultimate cat name sorter! Please help me decide on the perfect name by choosing your favorite option in each matchup.</p>
      </header>
      {!isSorting && !showResults && (
        <div className="user-input">
          <label htmlFor="userName">Enter your name:</label>
          <input type="text" id="userName" value={userName} onChange={(e) => setUserName(e.target.value)} placeholder="Your Name" required />
          <button onClick={handleStart}>Start Sorting</button>
        </div>
      )}
      {isSorting && (
        <div className="quiz-section">
          <h2>Matchup</h2>
          <div className="matchup">
            <button onClick={() => handleChoice('left')}>{nameOptions[currentIndex]}</button>
            <div className="middleField">
              <button onClick={() => handleChoice('both')}>I like both</button>
              <button onClick={() => handleChoice('none')}>No Opinion</button>
            </div>
            <button onClick={() => handleChoice('right')}>{nameOptions[currentIndex + 1]}</button>
          </div>
        </div>
      )}
      {showResults && (
        <div className="results-section">
          <h2>Results</h2>
          <ul>
            {results.map((result, index) => (
              <li key={index}>{result}</li>
            ))}
          </ul>
          <button onClick={handleRedo}>Redo Bracket</button>
        </div>
      )}
      <footer>
        <p>Made with ❤️ by a fellow cat lover</p>
      </footer>
    </div>
  );
}

export default App;
