import React from 'react';
import './TournamentSetup.css';

function TournamentSetup({ onStart, userName, existingRatings }) {
  const handleQuickStart = () => {
    // Default to 8 names for a quick tournament
    const numberOfNames = 8;
    const availableNames = Object.keys(existingRatings || {});
    const selectedNames = availableNames
      .sort(() => Math.random() - 0.5)
      .slice(0, numberOfNames);

    onStart(selectedNames);
  };

  return (
    <div className="tournament-setup">
      <div className="welcome-section">
        <h2>Welcome, {userName}! ðŸ‘‹</h2>
        <p>Before we start rating cat names, meet the cat who needs a name:</p>

        <div className="cat-gallery">
          <img src="/images/IMG_4844.jpg" alt="My cat 1" className="cat-photo" />
          <img src="/images/IMG_4845.jpg" alt="My cat 2" className="cat-photo" />
          <img src="/images/IMG_4846.jpg" alt="My cat 3" className="cat-photo" />
          <img src="/images/IMG_4847.jpg" alt="My cat 4" className="cat-photo" />
        </div>
      </div>

      <div className="setup-options">
        <h3>Ready to help pick a name?</h3>
        <button 
          onClick={handleQuickStart}
          className="quick-start-button"
        >
          Start Rating Names!
        </button>
      </div>
    </div>
  );
}

export default TournamentSetup;
