import React, { useState, useEffect } from 'react';
import { supabase } from '../../supabase/supabaseClient';
import './TournamentSetup.css';

function TournamentSetup({ onStart, existingRatings = [] }) {
  const [availableNames, setAvailableNames] = useState([]);
  const [selectedNames, setSelectedNames] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [validationError, setValidationError] = useState('');

  const MIN_NAMES = 2;

  useEffect(() => {
    fetchNames();
  }, []);

  const fetchNames = async () => {
    try {
      const { data, error } = await supabase
        .from('name_options')
        .select('name, description')
        .order('name');

      if (error) throw error;

      setAvailableNames(data);
      setIsLoading(false);
    } catch (err) {
      console.error('Error fetching names:', err);
      setError('Failed to load names. Please try again later.');
      setIsLoading(false);
    }
  };

  const toggleName = (name) => {
    setSelectedNames(prev => {
      if (prev.includes(name)) {
        const newSelection = prev.filter(n => n !== name);
        setValidationError(newSelection.length < MIN_NAMES ? 
          'At least 2 names are required' : '');
        return newSelection;
      }
      setValidationError('');
      return [...prev, name];
    });
  };

  const handleStart = () => {
    if (selectedNames.length < MIN_NAMES) {
      setValidationError('Please select at least 2 names to start the tournament.');
      return;
    }
    setValidationError('');
    onStart(selectedNames);
  };

  if (isLoading) return <div className="loading">Loading names...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="tournament-setup">
      <h2>Name Selection</h2>

      {validationError && (
        <div className="validation-error">
          {validationError}
        </div>
      )}

      <div className="name-count">
        Selected: {selectedNames.length} names
        {selectedNames.length > 0 && selectedNames.length < MIN_NAMES && (
          <span className="warning"> (Select at least {MIN_NAMES} names)</span>
        )}
      </div>

      <div className="names-grid">
        {availableNames.map(nameObj => (
          <div
            key={nameObj.name}
            onClick={() => toggleName(nameObj.name)}
            className={`name-card ${selectedNames.includes(nameObj.name) ? 'selected' : ''}`}
          >
            <div className="name-content">
              <h3 className="name-text">{nameObj.name}</h3>
              <p className="name-description">{nameObj.description}</p>
            </div>
            {selectedNames.includes(nameObj.name) && (
              <span className="check-mark">âœ“</span>
            )}
          </div>
        ))}
      </div>

      <div className="start-section">
        <button
          onClick={handleStart}
          className="start-button"
          disabled={selectedNames.length < MIN_NAMES}
        >
          Start Tournament with {selectedNames.length} Names
        </button>
      </div>
    </div>
  );
}

export default TournamentSetup;
