/**
 * @module Results
 * @description Main results component that displays the final rankings of cat names.
 * Shows the tournament results with ratings and provides option to restart.
 * 
 * @example
 * <Results 
 *   ratings={{ "Whiskers": 1450, "Mittens": 1380 }}
 *   onRestart={() => handleRestart()}
 * />
 * 
 * @component
 * @param {Object} props
 * @param {Object} props.ratings - Object mapping cat names to their Elo ratings
 * @param {Function} props.onRestart - Callback function to restart the tournament
 * @returns {JSX.Element} Results view with rankings and restart option
 */

import React, { useState } from 'react';
import ResultsTable from './ResultsTable';
import RankingAdjustment from '../RankingAdjustment/RankingAdjustment';
import './Results.css';

function Results({ ratings, onStartNew, userName, onUpdateRatings, currentTournamentNames }) {
  const [isAdjusting, setIsAdjusting] = useState(false);

  // Filter ratings to only show current tournament results
  const currentRatings = currentTournamentNames 
    ? Object.fromEntries(
        Object.entries(ratings || {}).filter(([name]) => 
          currentTournamentNames.includes(name)
        )
      )
    : ratings;

  const handleAdjustRankings = () => {
    setIsAdjusting(true);
  };

  const handleSaveAdjustments = (adjustedRankings) => {
    if (onUpdateRatings) {
      // Merge adjusted rankings with existing ratings
      const updatedRatings = { ...ratings };
      adjustedRankings.forEach(({ name, rating }) => {
        updatedRatings[name] = rating;
      });
      onUpdateRatings(adjustedRankings);
    }
    setIsAdjusting(false);
  };

  const handleCancelAdjustments = () => {
    setIsAdjusting(false);
  };

  // Convert ratings object to array format for RankingAdjustment
  const rankingsArray = Object.entries(currentRatings || {})
    .map(([name, rating]) => ({
      name,
      rating: typeof rating === 'number' ? rating : 1500
    }))
    .sort((a, b) => b.rating - a.rating); // Sort by rating descending

  if (isAdjusting) {
    return (
      <RankingAdjustment
        rankings={rankingsArray}
        onSave={handleSaveAdjustments}
        onCancel={handleCancelAdjustments}
      />
    );
  }

  return (
    <div className="results-container">
      <h2>Tournament Results</h2>
      <p className="results-info">
        Here are your rankings for this tournament, {userName}! You can adjust these rankings
        or start a new tournament with different names.
      </p>

      <ResultsTable ratings={currentRatings} />

      <div className="results-actions">
        <button 
          onClick={handleAdjustRankings} 
          className="adjust-button"
        >
          Adjust Rankings
        </button>
        <button 
          onClick={onStartNew} 
          className="primary-button"
        >
          Start New Tournament
        </button>
        <p className="results-tip">
          Starting a new tournament will let you rate more names while keeping your current rankings.
        </p>
      </div>
    </div>
  );
}

export default Results;
